// 1/30/2016 Aatish Bhatia (@aatishb)
var physics;
var particles = [];
var springs = [];
var stiffness = 0.2;

var points = [];
var lines = [];
var fixed_nodes = [];
var inputMode = 1;
var setupMode = 0;
var simulationMode = 0;

var button;


function setup() {
  createCanvas(600,600);
  background(51);
  textAlign(CENTER);
  textSize(20);
  stroke(200);
  strokeWeight(2);
  fill(255,0,0);
  button = createButton('Done');
  button.position(width-50, height-45);
  button.mousePressed(buttonPressed);

}

function draw() {
  
  if(inputMode){
    // input mode draws your mesh and lets you select fixed points
    drawMesh();
  }

  if(setupMode){
    // setup mode initalizes the physics world (it only runs once)
    button.remove();
    initializePhysics();
    setupMode = 0;
    simulationMode = 1;
  }

  if(simulationMode){
    // simulation mode runs the simulation i.e. updates the physics world and displays your mesh
    simulate();
  }

}
  
  
function drawMesh(){
  background(51);
  for(var i = 0; i < mesh.length; i++){

    var x1 = x_scale*mesh[i][0]+x_shift;
    var y1 = y_scale*mesh[i][1]+y_shift;
    var x2 = x_scale*mesh[i][2]+x_shift;
    var y2 = y_scale*mesh[i][3]+y_shift;

    line(x1,y1,x2,y2);
    
    if(abs(mouseX-x1)<5 && abs(mouseY-y1)<5){
      ellipse(x1,y1,10,10);
      var point = [x1,y1];
      if(mouseIsPressed && !fixed_nodes.containsArray(point)){
        fixed_nodes.push(point);
      }
    }
    else if(abs(mouseX-x2)<5 && abs(mouseY-y2)<5){
      ellipse(x2,y2,10,10);
      var point = [x2,y2];
      if(mouseIsPressed && !fixed_nodes.containsArray(point)){
        fixed_nodes.push(point);
      }
    }

  }

  for(var i = 0; i < fixed_nodes.length; i++){
    ellipse(fixed_nodes[i][0],fixed_nodes[i][1],10,10);
  }
  noStroke();
  text('Click on the nodes you want to fix',width/2,height-30);
  stroke(200);
  strokeWeight(2);

  
}

function buttonPressed() {

  inputMode = 0;
  setupMode = 1;
  
}


function initializePhysics(){
  
    // Initialize the physics
  physics=new VerletPhysics2D();
  physics.addBehavior(new GravityBehavior(new Vec2D(0,0.5)));

  // Set the world's bounding box
  physics.setWorldBounds(new Rect(0,0,width,height));
  
  // Make particles
  for(var i = 0; i<mesh.length; i++){

    var x1 = x_scale*mesh[i][0]+x_shift;
    var y1 = y_scale*mesh[i][1]+y_shift;
    var x2 = x_scale*mesh[i][2]+x_shift;
    var y2 = y_scale*mesh[i][3]+y_shift;

    // Check if particle already exists at that location
    if (points.indexOf(str(x1)+':'+str(y1)) < 0) {
      // If not, create the particle
      particles.push(new Particle(new Vec2D(x1,y1)));
      // Anything we make, we have to add into the physics world
      physics.addParticle(particles.slice(-1)[0]);
      // Index new particles by their location
      points.push(str(x1)+':'+str(y1));
    }

    if (points.indexOf(str(x2)+':'+str(y2)) < 0) {
      particles.push(new Particle(new Vec2D(x2,y2)));
      physics.addParticle(particles.slice(-1)[0]);
      points.push(str(x2)+':'+str(y2));
    }

    var i1 = points.indexOf(str(x1)+':'+str(y1));
    var i2 = points.indexOf(str(x2)+':'+str(y2));

    for(var j = 0; j<fixed_nodes.length; j++){
      if(x1==fixed_nodes[j][0] && y1==fixed_nodes[j][1]){
        particles[i1].lock();
      }
      if(x2==fixed_nodes[j][0] && y2==fixed_nodes[j][1]){
        particles[i2].lock();
      }

    }

    // Make a spring connecting both Particles
    springs.push(new VerletSpring2D(particles[i1],particles[i2],dist(x1,y1,x2,y2),stiffness));
    physics.addSpring(springs.slice(-1)[0]);
    //line(x1,y1,x2,y2);
    lines.push([x1,y1,x2,y2]);
  }

  
}





function simulate(){
  
  // Update the physics world
  physics.update();

  background(51);

  // This next bit draws lines between the particles

  for(var i=0; i<lines.length; i++){

    var x1 = lines[i][0];
    var y1 = lines[i][1];
    var x2 = lines[i][2];
    var y2 = lines[i][3];
    
    var i1 = points.indexOf(str(x1)+':'+str(y1));
    var i2 = points.indexOf(str(x2)+':'+str(y2));

    line(particles[i1].x,particles[i1].y,particles[i2].x,particles[i2].y);
  
  
  }
  
  
}


Array.prototype.containsArray = function(val) {
    var hash = {};
    for(var i=0; i<this.length; i++) {
        hash[this[i]] = i;
    }
    return hash.hasOwnProperty(val);
}